function context(){[].forEach.call(document.querySelectorAll("[breakpoints]"),function(t){if(null!==t.getAttribute("class")){var s=t.getAttribute("class").split(" ")
s.forEach(function(s){("lt"==s.substring(0,2)||"gt"==s.substring(0,2)||"eq"==s.substring(0,2))&&t.classList.remove(s)})}var e=t.offsetWidth,i=t.getAttribute("breakpoints").split(" ")
i.forEach(function(s){e<parseFloat(s)?t.classList.add("lt"+s):e>parseFloat(s)?t.classList.add("gt"+s):t.classList.add("eq"+s)})})}context(),window.addEventListener("resize",function(){context()})
